// Fixed and simplified version of the original code, removing obfuscation and improving clarity

(function () {
  'use strict';

  // Prevent right-click, text selection, and drag
  document.addEventListener('contextmenu', function (e) {
    e.preventDefault();
    return false;
  });
  document.addEventListener('selectstart', function (e) {
    e.preventDefault();
    return false;
  });
  document.addEventListener('dragstart', function (e) {
    e.preventDefault();
    return false;
  });

  // Block certain key combinations (F12, Ctrl+Shift+I/J/C, Ctrl+U/S/A)
  document.addEventListener('keydown', function (e) {
    // F12
    if (e.keyCode === 123) {
      e.preventDefault();
      return false;
    }
    // Ctrl+Shift+I
    if (e.ctrlKey && e.shiftKey && e.keyCode === 73) {
      e.preventDefault();
      return false;
    }
    // Ctrl+Shift+J
    if (e.ctrlKey && e.shiftKey && e.keyCode === 74) {
      e.preventDefault();
      return false;
    }
    // Ctrl+U
    if (e.ctrlKey && e.keyCode === 85) {
      e.preventDefault();
      return false;
    }
    // Ctrl+S
    if (e.ctrlKey && e.keyCode === 83) {
      e.preventDefault();
      return false;
    }
    // Ctrl+A
    if (e.ctrlKey && e.keyCode === 65) {
      e.preventDefault();
      return false;
    }
    // Ctrl+Shift+C
    if (e.ctrlKey && e.shiftKey && e.keyCode === 67) {
      e.preventDefault();
      return false;
    }
  });

  // Console warning messages
  console.clear();
  console.log('%c⚠️ WARNING ⚠️', 'color: red; font-size: 20px; font-weight: bold;');
  console.log(
    '%cThis is a browser feature intended for developers. Do not enter any code or give access to anyone you do not trust.',
    'color: red; font-size: 14px;'
  );
  console.log(
    '%cUnauthorized access attempts are being monitored and logged.',
    'color: orange; font-size: 12px;'
  );

  // Disable all console methods except for this warning
  var disabledConsoleMethods = [
    'log', 'debug', 'info', 'warn', 'error', 'assert', 'clear', 'count', 'dir', 'dirxml',
    'group', 'groupCollapsed', 'groupEnd', 'profile', 'profileEnd', 'time', 'timeEnd',
    'timeStamp', 'trace'
  ];
  disabledConsoleMethods.forEach(function (method) {
    if (method !== 'log') {
      console[method] = function () {};
    }
  });

  // Detect DevTools by checking window size differences
  var devToolsOpen = false;
  var threshold = 160;
  setInterval(function () {
    if (
      window.outerWidth - window.innerWidth > threshold ||
      window.outerHeight - window.innerHeight > threshold
    ) {
      if (!devToolsOpen) {
        devToolsOpen = true;
        console.clear();
        console.log('%c⚠️ DEVELOPER TOOLS DETECTED ⚠️', 'color: red; font-size: 20px; font-weight: bold;');
        console.log('%cAccess to developer tools is not allowed on this website.', 'color: red; font-size: 14px;');
      }
    } else {
      devToolsOpen = false;
    }
  }, 500);

  // Prevent image dragging via keyup on images
  document.addEventListener('dragstart', function (e) {
    if (e.target.tagName === 'IMG') {
      e.preventDefault();
      return false;
    }
  });

  // Prevent "Print Screen" key (keyCode 44)
  document.addEventListener('keyup', function (e) {
    if (e.keyCode === 44) {
      e.preventDefault();
      return false;
    }
  });
})();